/* ***FIRST EXECUTABLE STATEMENT  DSTODE */
    /* Parameter adjustments */
    --neq;
    --y;
    yh_dim1 = *nyh;
    yh_offset = 1 + yh_dim1;
    yh -= yh_offset;
    --yh1;
    --ewt;
    --savf;
    --acor;
    --wm;
    --iwm;

    /* Function Body */
    dls001_3.kflag = 0;
    told = dls001_3.tn;
    ncf = 0;
    dls001_3.ierpj = 0;
    dls001_3.iersl = 0;
    dls001_3.jcur = 0;
    dls001_3.icf = 0;
    delp = 0.;
    if (dls001_3.jstart > 0) {
	goto L200;
    }
    if (dls001_3.jstart == -1) {
	goto L100;
    }
    if (dls001_3.jstart == -2) {
	goto L160;
    }
