    dls001_3.nq = 1;
    dls001_3.l = 2;
    dls001_3.ialth = 2;
    dls001_3.rmax = 1e4;
    dls001_3.rc = 0.;
    dls001_3.el0 = 1.;
    dls001_3.crate = .7;
    dls001_3.hold = dls001_3.h__;
    dls001_3.meo = dls001_3.meth;
    dls001_3.nslp = 0;
    dls001_3.ipup = dls001_3.miter;
    iret = 3;
    goto L140;
