/* ----------------------------------------------------------------------- */
/* If H is being changed, the H ratio RH is checked against */
/* RMAX, HMIN, and HMXI, and the YH array rescaled.  IALTH is set to */
/* L = NQ + 1 to prevent a change of H for that many steps, unless */
/* forced by a convergence or error test failure. */
/* ----------------------------------------------------------------------- */
L160:
    if (dls001_3.h__ == dls001_3.hold) {
	goto L200;
    }
    rh = dls001_3.h__ / dls001_3.hold;
    dls001_3.h__ = dls001_3.hold;
    iredo = 3;
    goto L175;
L170:
/* Computing MAX */
    d__1 = rh, d__2 = dls001_3.hmin / abs(dls001_3.h__);
    rh = max(d__1,d__2);
L175:
    rh = min(rh,dls001_3.rmax);
/* Computing MAX */
    d__1 = 1., d__2 = abs(dls001_3.h__) * dls001_3.hmxi * rh;
    rh /= max(d__1,d__2);
    r__ = 1.;
    i__1 = dls001_3.l;
    for (j = 2; j <= i__1; ++j) {
	r__ *= rh;
	i__2 = dls001_3.n;
	for (i__ = 1; i__ <= i__2; ++i__) {
/* L180: */
	    yh[i__ + j * yh_dim1] *= r__;
	}
    }
    dls001_3.h__ *= rh;
    dls001_3.rc *= rh;
    dls001_3.ialth = dls001_3.l;
    if (iredo == 0) {
	goto L690;
    }
/* ----------------------------------------------------------------------- */
/* This section computes the predicted values by effectively */
/* multiplying the YH array by the Pascal Triangle matrix. */
/* RC is the ratio of new to old values of the coefficient  H*EL(1). */
/* When RC differs from 1 by more than CCMAX, IPUP is set to MITER */
/* to force PJAC to be called, if a Jacobian is involved. */
/* In any case, PJAC is called at least every MSBP steps. */
/* ----------------------------------------------------------------------- */
L200:
    if ((d__1 = dls001_3.rc - 1., abs(d__1)) > dls001_3.ccmax) {
	dls001_3.ipup = dls001_3.miter;
    }
    if (dls001_3.nst >= dls001_3.nslp + dls001_3.msbp) {
	dls001_3.ipup = dls001_3.miter;
    }
    dls001_3.tn += dls001_3.h__;
    i1 = dls001_3.nqnyh + 1;
    i__2 = dls001_3.nq;
    for (jb = 1; jb <= i__2; ++jb) {
	i1 -= *nyh;
/* dir$ ivdep */
	i__1 = dls001_3.nqnyh;
	for (i__ = i1; i__ <= i__1; ++i__) {
/* L210: */
	    yh1[i__] += yh1[i__ + *nyh];
	}
/* L215: */
    }
/* ----------------------------------------------------------------------- */
/* Up to MAXCOR corrector iterations are taken.  A convergence test is */
/* made on the R.M.S. norm of each correction, weighted by the error */
/* weight vector EWT.  The sum of the corrections is accumulated in the */
/* vector ACOR(i).  The YH array is not altered in the corrector loop. */
/* ----------------------------------------------------------------------- */
L220:
    m = 0;
    i__2 = dls001_3.n;
    for (i__ = 1; i__ <= i__2; ++i__) {
/* L230: */
	y[i__] = yh[i__ + yh_dim1];
    }
    (*f)(&neq[1], &dls001_3.tn, &y[1], &savf[1]);
    ++dls001_3.nfe;
    if (dls001_3.ipup <= 0) {
	goto L250;
    }
/* ----------------------------------------------------------------------- */
/* If indicated, the matrix P = I - h*el(1)*J is reevaluated and */
/* preprocessed before starting the corrector iteration.  IPUP is set */
/* to 0 as an indicator that this has been done. */
/* ----------------------------------------------------------------------- */
    (*pjac)(&neq[1], &y[1], &yh[yh_offset], nyh, &ewt[1], &acor[1], &savf[1], 
	    &wm[1], &iwm[1], (S_fp)f, (U_fp)jac);
    dls001_3.ipup = 0;
    dls001_3.rc = 1.;
    dls001_3.nslp = dls001_3.nst;
    dls001_3.crate = .7;
    if (dls001_3.ierpj != 0) {
	goto L430;
    }
L250:
    i__2 = dls001_3.n;
    for (i__ = 1; i__ <= i__2; ++i__) {
/* L260: */
	acor[i__] = 0.;
    }
L270:
    if (dls001_3.miter != 0) {
	goto L350;
    }
/* ----------------------------------------------------------------------- */
/* In the case of functional iteration, update Y directly from */
/* the result of the last function evaluation. */
/* ----------------------------------------------------------------------- */
    i__2 = dls001_3.n;
    for (i__ = 1; i__ <= i__2; ++i__) {
	savf[i__] = dls001_3.h__ * savf[i__] - yh[i__ + (yh_dim1 << 1)];
/* L290: */
	y[i__] = savf[i__] - acor[i__];
    }
    del = dvnorm_(&dls001_3.n, &y[1], &ewt[1]);
    i__2 = dls001_3.n;
    for (i__ = 1; i__ <= i__2; ++i__) {
	y[i__] = yh[i__ + yh_dim1] + dls001_3.el[0] * savf[i__];
/* L300: */
	acor[i__] = savf[i__];
    }
    goto L400;
/* ----------------------------------------------------------------------- */
/* In the case of the chord method, compute the corrector error, */
/* and solve the linear system with that as right-hand side and */
/* P as coefficient matrix. */
/* ----------------------------------------------------------------------- */
L350:
    i__2 = dls001_3.n;
    for (i__ = 1; i__ <= i__2; ++i__) {
/* L360: */
	y[i__] = dls001_3.h__ * savf[i__] - (yh[i__ + (yh_dim1 << 1)] + acor[
		i__]);
    }
    (*slvs)(&wm[1], &iwm[1], &y[1], &savf[1]);
    if (dls001_3.iersl < 0) {
	goto L430;
    }
    if (dls001_3.iersl > 0) {
	goto L410;
    }
    del = dvnorm_(&dls001_3.n, &y[1], &ewt[1]);
    i__2 = dls001_3.n;
    for (i__ = 1; i__ <= i__2; ++i__) {
	acor[i__] += y[i__];
/* L380: */
	y[i__] = yh[i__ + yh_dim1] + dls001_3.el[0] * acor[i__];
    }
/* ----------------------------------------------------------------------- */
/* Test for convergence.  If M.gt.0, an estimate of the convergence */
/* rate constant is stored in CRATE, and this is used in the test. */
/* ----------------------------------------------------------------------- */
L400:
    if (m != 0) {
/* Computing MAX */
	d__1 = dls001_3.crate * .2, d__2 = del / delp;
	dls001_3.crate = max(d__1,d__2);
    }
/* Computing MIN */
    d__1 = 1., d__2 = dls001_3.crate * 1.5;
    dcon = del * min(d__1,d__2) / (dls001_3.tesco[dls001_3.nq * 3 - 2] * 
	    dls001_3.conit);
    if (dcon <= 1.) {
	goto L450;
    }
    ++m;
    if (m == dls001_3.maxcor) {
	goto L410;
    }
    if (m >= 2 && del > delp * 2.) {
	goto L410;
    }
    delp = del;
    (*f)(&neq[1], &dls001_3.tn, &y[1], &savf[1]);
    ++dls001_3.nfe;
    goto L270;
/* ----------------------------------------------------------------------- */
/* The corrector iteration failed to converge. */
/* If MITER .ne. 0 and the Jacobian is out of date, PJAC is called for */
/* the next try.  Otherwise the YH array is retracted to its values */
/* before prediction, and H is reduced, if possible.  If H cannot be */
/* reduced or MXNCF failures have occurred, exit with KFLAG = -2. */
/* ----------------------------------------------------------------------- */
L410:
    if (dls001_3.miter == 0 || dls001_3.jcur == 1) {
	goto L430;
    }
    dls001_3.icf = 1;
    dls001_3.ipup = dls001_3.miter;
    goto L220;
L430:
    dls001_3.icf = 2;
    ++ncf;
    dls001_3.rmax = 2.;
    dls001_3.tn = told;
    i1 = dls001_3.nqnyh + 1;
    i__2 = dls001_3.nq;
    for (jb = 1; jb <= i__2; ++jb) {
	i1 -= *nyh;
/* dir$ ivdep */
	i__1 = dls001_3.nqnyh;
	for (i__ = i1; i__ <= i__1; ++i__) {
/* L440: */
	    yh1[i__] -= yh1[i__ + *nyh];
	}
/* L445: */
    }
