    if (dls001_3.ialth > 1) {
	goto L700;
    }
    if (dls001_3.l == dls001_3.lmax) {
	goto L700;
    }
    i__1 = dls001_3.n;
    for (i__ = 1; i__ <= i__1; ++i__) {
/* L490: */
	yh[i__ + dls001_3.lmax * yh_dim1] = acor[i__];
    }
